INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('EZI Coffee', 'Nghe đến quán này nổi tiếng ngon với trà sữa trân châu đường đen nên mình cùng đồng nghiệp có order thử ở đây về uống như thế nào. 
+ Về dịch vụ của Now : vì lúc order là tầm 2h nên có nhắn với call center &amp; shipper là tầm 3h30 đã giao đến (chứ giao đến sớm thì chưa uống được để đá tan hết sợ dở) , nhưng shipper gọi qua nói là quán đã làm rồi nên tầm 3h kém giao hàng đến. Ok tạm chấp nhận . Vì order thiếu 1 ly nên mình nhắn cho call center là có thể cho shipper mua thêm 1 ly rồi mình đưa tiền thêm được không, nhưng chị call center khăng khăng nói là ko được vì đã trả tiền cho quán rồi . (Trong khi mình order các quán khác đều mình đều có thể nhờ mua thêm được ) .  &gt; Không hài lòng
+ Về shipper : Sau đó mình gọi cho shipper, coi như mình nhờ anh đó mua  giúp mình rồi mình sẽ trả tiền. Ảnh lại nói là ko mua được vì lý do "không còn chổ để bỏ nữa rồi" . &gt; Không hài lòng.
+ Về quán EZI : mình order rất nhiều ly và nhiều loại nhưng quán lại ko hề dán một nhãn nào cho từng ly. Nếu order 1 2 ly cùng loại thì ko nói gì, ở đây mình order rất nhiều loại mà ko hề ghi từng nhãn làm ai cũng hoang mang, ngồi đoán thử ly nào loại nào.  Còn thêm nữa, quán làm nhầm của mình rất nhiều ly, thiếu trà sữa trân châu đường đen và nhầm qua loại trà khác ( như trong hình , không biết đó là loại gì nữa )
+ Về chất lượng : Tạm ổn, riêng loại trà màu vàng vàng như hình ( không biết loại gì vì không ghi nhãn và hình như mình cũng ko order loại này) chỉ có vị như nước trà + vị hoa nhài + rất đắng.
Nói chung order 1 lần và mãi mãi không bao giờ order nữa', 7,14, '30 Ông Ích Khiêm, Quận Hải Châu, Đà Nẵng', to_timestamp('1555083635'), to_timestamp('1563429528'), to_timestamp('1563429528'));
INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Yi He Tang - Chi Lăng', 'Trà sữa nướng 1 thời nổi đình đám ở Hà Nội nay đã có ở ĐN rồi nè. Đang soft opening nên được giảm 20% khi mua trực tiếp ở quán và 30% khi đặt qua Now. 
Mình đi buổi chiều nên cũng không đông lắm order khoảng 5’ có ngay. Từ bảo vệ tới nhân viên đều rất lịch sự thân thiện, quán cũng mát mẻ sạch sẽ. Mình gọi 1 hồng trà sữa nướng và 1 trà xanh sữa nướng size L bảo giảm 70 đường thôi mà các bạn ý chắc ‘lỡ tay’ tự giảm cả đá còn 50 luôn 😹
Nghe đồn trà này ngọt lắm mọi người đều khuyên nên giảm còn 30 đường thôi nhưng mình gọi 70 đường đem đi 1 đoạn thì đá tan ra nên thấy trà ngọt vừa, nhạt nhạt rồi. Nghe danh mãi giờ mới được thử nên háo hức lắm mà uống xong thấy chả khác gì trà sữa bình thường cả đã thế còn ít nữa xé nắp ra thấy có 2/3 li không biết size M thì thế nào 😹 mặc dù được giảm 20% rồi nhưng vẫn thấy hơi xót tiền hic. 
Dù sao mọi người cứ gọi 1 li size M uống thử trước xem sao vì khẩu vị mỗi người mỗi khác mình k thấy ngon mà có thể người khác lại thấy ngon.', 3,5, '40 Chi Lăng, Quận Hải Châu, Đà Nẵng', to_timestamp('1563596050'), to_timestamp('1563769850'), to_timestamp('1563769850'));
INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Ốc 39 Ăn Là Ghiền', 'Ốc rất tươi, sốt ngon ơi là ngon. Chấm bánh mỳ tuyệt cú mèo. Năm ngoái tìm được trên foody, nên bọn mình đến ăn thử. Chỗ hơi xa nên có dịp đi hội an mới ghé vào ăn được. Năm này lại có dịp dưa đứa em đi tiếp, nên lại ghé. Dù là 1 năm r nhưng quán vẫn không giảm nhiệt chút nào. Phong độ vẫn như xưa. Anh chị chủ quán dễ thương giới thiệu cho mình những loại ốc lợi hại của quán. Mình gọi mỗi loại ốc 1 sốt để ăn cho đa dạng. Ốc cà na sốt bơ tỏi, ốc hương rang muối, ốc mỡ sốt cay, ốc bông nữa, với 1 đĩa hàu nướng. Thêm bánh mỳ nc ngọt. Tổng cộng 260k . Tính ra mỗi ng chưa đến 100k. Rẻ mà ngon. Hẹn có dịp đi hội an lại ghé tiếp. Tiếc là lo ăn quá quên luôn chụp hình..:(', 8,12, '39 Nguyễn Tạo, Quận Ngũ Hành Sơn, Đà Nẵng', to_timestamp('1542283960'), to_timestamp('1563460623'), to_timestamp('1563460623'));
INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Hantok Đà Nẵng - Món Hàn', 'Không gian quán tương đối ổn nhưng hơi nóng tầng trên là phòng kín điều hoà chỉ có một cái mà đồ ăn lại nóng nên hơi khó chịu. Đồ ăn quán cũng được chắc tại mình ăn trúng mấy món không hợp. Mình gọi 1 combo A ăn 2 người mà ăn không hết no quá trời luôn ấy. Nước trong combo có 2 ly nhưng không đủ mình vẫn gọi thêm 2 lon nước nữa. Cực kì thích nhân viên phục vụ. Khách đông trời nóng trên mặt ai cũng hiện nét mệt nhưng k nhăn nhó với khách, nói chuyện lại duyên duyên thế nào ấy, k chỉ 1 người đâu nha mà là tất cả, đồ ăn thì không hài lòng cho lắm nhưng mà nhân viên thì quán này là ok nhất trong tất cả các quán mình từng đi. À trước khi đi mình có tìm hiểu quán nhưng khi qua thì menu thay lạ hoắc mình hỏi thì được biết quán mới đổi lại toàn bộ menu. Mọi người lưu ý nhé :)', 1,7, 'Tầng 2, Lô A1 Nguyễn Văn Linh, Quận Hải Châu, Đà Nẵng', to_timestamp('1504094398'), to_timestamp('1530946186'), to_timestamp('1530946186'));
INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Bánh Xèo Tôm Nhảy Năm Hiền', 'Ngày xưa mình hay ăn cô Ba, mà giờ chỗ này ra Phan Thanh rồi, gần mình nữa nên mình ăn hoài lun.
