INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Papas'' Chicken Restaurant', 'Đây là quán mình vs bạn trai hay ăn nhiều lần,  không bàn tới chất lượng món ăn mà mình muốn đề cập đến cách phục vụ nhân vien quán , trước giờ  ở đây phục vụ cũng ổn, gọi món mang ra nhanh. Nhưng đó là khi đến vào ngày thường , còn ngày lễ thì chuyện khác, cụ thể ngày hôm nay 14/2 khi tới quán để ăn tối, quán đã thực sự làm mình thất vọng. Tụi mình tới lúc 20h tối có order 3 món nhưng các bạn phục vụ để bọn mình đợi cả tiếng đồng hồ mà chưa có món, trong khi những khách sau tới sau bọn mình thì lại đc mang món ra trước. Là người từng làm nhà hàng khách sạn mình ko hài lòng về cách phục vụ của nhân viên, nói thẳng ra nhân viên quán ko biết cách phục vụ, order bàn này trước tới lúc mang món ra lại của bàn vào sau, tới khi đợi lâu quá tụi mình mới gọi phục vụ tới thì lại nhận đc câu nói hời hợt dạ rồi bỏ chạy mất, đến lúc mình bực quá kêu tính tiền thì chạy lại cười nói "dạ tui em hủy bill rồi" mà ko có lấy 1 câu xin lỗi kh, hết nói nổi lun. Tụi mình đi ăn cũng  bỏ tiền ra để thưởng thức vậy mà lại nhận đc thái độ phục vụ hời hợt như vậy sao. Mình muốn chia sẻ trải nghiệm cho những bạn nào nếu chưa tới quán này thì chừa quán này ra luôn chứ thấy cách phục vụ + thái độ nhân viên vậy thì thà ăn những chỗ bình dân còn đỡ hơn. Từ nay sẽ "cạch" luôn papa chicken nhé!!', 2,9, 'Lô 6.C1 - 7.C1 Phạm Văn Đồng, Quận Sơn Trà, Đà Nẵng', to_timestamp('1540597876'), to_timestamp('1563169955'), to_timestamp('1563169955'));
