INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Vũ Xuyên Quán - Hải Sản', 'Mình đi ăn nhiều nơi và thật sự phải nói chỗ này phục vụ siêu tệ, nhân viên mất lịch sự nhất hệ mặt trời. Lúc mình đến quán vắng nên cũng ko thể đổ lỗi do khách đông được ha. Mình gọi một chip chip hấp và vẹm xanh nướng, chờ cũng phải 30 phút nhân viên đem vẹm ra hất hàm nói một câu "Hết chip chip rồi, gọi cái khác nhanh". Mình đổi qua ốc gai, bạn mình hỏi món này còn không thì nhân viên cũng y cái giọng vậy nói "chưa biết, vô bếp xem mới biết" . Rồi sau đó mình và bạn đợi rất rất lâu, hai đứa sốt ruột muốn về, hỏi đúng nhân viên đó là nếu chưa làm thì tính tiền cho tụi mình về, bạn nhân viên đó nhìn trân trân tụi mình rồi làm thinh tiếp tục xếp bia vào thùng trong khi đứng cách tụi mình khoảng 1m. Bạn mình sốt ruột chạy vào bếp hỏi thì lúc đó cô trong nhà bếp mới nói là ốc gai vỏ dày hấp lâu.

Chưa hết, lúc tính tiền thì bạn mình có gọi 3 chai bia nhưng chỉ uống 2 chai, bạn mình mới nói là "em chỉ uống hai chai nên bớt lại 10k anh nha", bạn nhân viên đó cố ý không nghe, tới khi bọn mình đưa tiền thì quay lại nói gay với bạn mình "đưa thiếu tiền rồi nè"

Bọn mình trước giờ đi đâu cũng vô cùng lịch sự với nhân viên phục vụ, thậm chí bạn mình vẫn thường hay cho thêm tiền cho phục vụ nếu họ thân thiện nhiệt tình, bọn mình đi ăn trả tiền chứ ko phải đi ăn xin, thà bỏ thêm một số tiền mà service tốt. Cảm thấy tiếc cho quán vì ngày xưa mình làm ở một công ty gần quán thường xuyên ra đây ăn với anh chị đồng nghiệp, đồ ăn rất ngon mà nhân viên lại nhanh nhẹn lịch sự lắm.', 1,8, 'Dương Đình Nghệ, Quận Sơn Trà, Đà Nẵng', to_timestamp('1463059001'), to_timestamp('1553483220'), to_timestamp('1553483220'));
INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Quán Mậu - Bánh Tráng Thịt Heo', 'Tình cờ vào ăn quán này. Thấy cũng ổn, làm theo mô hình cho khách du lịch. Phần như này 70k mình thấy hơi đắt. Thêm gì cũng tính ra tiền được, đến cái bánh cuốn mỏng dính xin thêm ăn cho hết 2 miếng thịt cũng tính tiền. Phần thịt mình thấy cũng bình thường, không ăn được mắm kia nên mình ăn mắm tỏi ớt. Mắm tỏi ớt không thơm mà pha còn hơi mặn nữa. 
Nhân viên cũng nhiệt tình nhưng cái khăn trải bàn ý. Thôi thà k trải, ăn xong giọt mắm cả khăn cũng chỉ dọn rác thôi. Mắm vẫn dính đó. Dọn hết rồi mà ruồi vẫn bay bay nhìn hơi ghê.
Ăn xong dọn dĩa dưa 6 hay 8 miếng bé bé gì đó, thấy cũng tử tế :))) thì ra lúc tính tiền tính 10k :)) hỏi thì bảo e dọn ra mọi người ăn thì tính tiền thôi :)) ơ. Tính tiền thì để ngta gọi rồi đem ra chớ tự đem ra vậy ai biết :)) lam như kiểu mấy quán nhậu ngoài biển bưng dĩa đậu, trứng ra để đó ngta ăn thì tính tiền không bằng :))', 5,10, '35 Đỗ Thúc Tịnh, Quận Cẩm Lệ, Đà Nẵng', to_timestamp('1534913915'), to_timestamp('1563712402'), to_timestamp('1563712402'));
INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Bún Riêu Cua 8 - Yên Bái', 'Quán bún riêu cua này theo mình là ngon nhất trong những quán mình từng ăn. Mình ăn ở đây từ hồi quán ở vỉa hè ngoài mặt tiền đường Yên Bái, giờ thì chuyển vào trong hẻm nhỏ rồi. 1 tô lớn 22k, tô nhỏ 20k , tô đặc biệt nhiều riêu, chả , giá 25k. Lâu lâu ghé lại ăn, vẫn hương vị cũ, ăn ngon đã thèm dễ sợ. 1  tô có chả , riêu cua cũng khá nhiều, nước bún đậm đà vị riêu cua ăn vừa miệng. Có kèm 1 tô rau sống riêng. Bàn ghế cao ráo, sạch sẽ, có điều là mùa hè ngồi hơi nóng. Quán tuy ở trong hẻm nhưng lúc nào cũng đông khách hết nghe. Phục vụ nhanh, nhiệt tình. Ngoài ra còn có sữa đậu nành và sữa đậu xanh cũng ngon lắm đó. Chỗ để xe hơi chật tí nhưng có 1 chú đứng ngoài dắt xe giúp nên thích lắm.', 14,14, '10/15 Yên Bái (Vào Kiệt Rẽ Tay Trái), Quận Hải Châu, Đà Nẵng', to_timestamp('1407903425'), to_timestamp('1563712499'), to_timestamp('1563712499'));
INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Cơm Gà Bà Nguyệt - Lê Đình Dương', 'Đây là 1 trong những món đáng ăn ở Đà nẵng. Hạt cơm thơm lắm ý. Thơm và bùi đến mức độ cho mình ăn cơm ko cũng đc. Món gà xé trộn ngon, hợp miệng. Gà kho cũng ngon. Sợ mập bình thường ko dám ăn cơm mà hôm đó mình làm cho 1 đĩa bự. Quán ăn hơi giống quán gia đình. Hôm mình tới ăn ko đông chỉ có mỗi bàn mình mà đồ ăn lên hơi chậm. Cách phục vụ thì ko có gì đáng nói. Nếu tới Đn thì đây sẽ là địa điểm mà mình sẽ ghé lại.', 13,5, '40 Lê Đình Dương, P. Phước Ninh, Quận Hải Châu, Đà Nẵng', to_timestamp('1525166830'), to_timestamp('1563716168'), to_timestamp('1563716168'));
