INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Joy Chè', 'Thấy review trên page fb của foody nên order thử. Mình đặt 1 chè khoai dẻo hạt sen dừa non và 1 chè khoai dẻo đậu. Ấn tượng đầu tiên là đóng gói, siêu xinh, cảm giác ấn tượng ban đầu rất tốt. Lượng chè thì phải nói là khá nhiều, còn đang được giảm giá trên foody nữa thì tính ra là siêu rẻ. Chè ngọt vừa, thơm béo. Khoai dẻo mềm dai vừa phải, cục nào cục nấy bự ăn đã miệng. Hạt sen nấu mềm, k nát. Mình có thêm vào chè hạt đác và trân châu. Hạt đác rim ngọt vừa phải, dai dai. Với 1 đứa thích ăn trân châu mềm như mình thì trân châu hơi dai, bạn nào thích ăn trân châu dai dai thì rất ngon. Nói chung là chè ở đây không làm mình thất vọng, sẽ ủng hộ quán và thử thêm những món chè khác. ❤', 9,13, '143 Tiểu La, Quận Hải Châu, Đà Nẵng', to_timestamp('1561642217'), to_timestamp('1563715981'), to_timestamp('1563715981'));
INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Buffet Nướng Restaurant', 'Đi ăn vài lần ở quán, lúc mới mở nhiều đồ ăn chất lượng hợp khẩu vị, hải sản nhiều tươi, dù chưa đông khách nhưng cảm thấy rất tuyệt. Mình đã rủ bạn bè minh đi ăn luôn. Nhưng kể từ khi lên foody quán đông hơn hẳn, rồi mới đây đi ăn lại cùng nhóm bạn gái mình cảm thấy thất vọng nhiều vì khi món ăn hết nhân viên thêm đồ ăn rất chậm mà ít, quán thì đông làm mình đứng chờ cả buổi. Gia vị cũng mặn hơn, tôm hàu thì dù 8g nhưng đã hết hay nhân viên không thêm thì mình không biết :( nhưng cảm thấy khi quán đông không còn chất lượng như xưa, quay lại thì cần cân nhắc, mong quán xem xét lại!', 10,11, '899 Nguyễn Tất Thành, Quận Thanh Khê, Đà Nẵng', to_timestamp('1560858431'), to_timestamp('1563715495'), to_timestamp('1563715495'));
INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('4 Saisons - Drink & Cream', 'Tình cờ thấy quán này nhìn dễ thương nên mò vào thử. Té ra là quán mới, sắp khai trương. Quán ko gian đèn vàng ấm áp, ghế dựa thoải mái. Có cây xanh ở mỗi bàn nữa. 
Menu giá cũng bình thường so vs mặt bằng ở phan châu trinh. Hai đứa mình gọi 1 trà dứa vs 1 trà sữa socola. Anh chủ quán có tặng thêm 1 phần chuối sấy cho bàn mình nữa. Chuối sấy ăn rất ngon :3 giòn rụm. Ngày xưa còn nhỏ mình mê món này lắm :)) trà dứa thì ko ngon, té ra dứa là lá dứa chứ ko phải quả dứa, quả thơm mình nghĩ. Gọi là trà nhưng chưa có vị trà nơi, thơm mùi lá dứa nhưng bị ngọt quá, có hạt đường khuấy chưa tan nữa. Trà sữa socola thì cũng ko được ngon. Mùi y sữa ovaltine mình ghét ~~ vs cả 1 ly trà nhưng ko có topping chi cả. Cả 2 món bị nhiều đá quá. 
Chắc là quán mới mở nên pha chế chưa ổn thôi. Bạn mình có góp ý với anh thu tiền ở quán rồi. Thái độ mọi người ở đây thân thiện. Nên cũng có thiện cảm dù đồ uống ko ngon, mình vs bạn bỏ lại hơn 1 nửa :3', 4,10, '240 Phan Châu Trinh, Quận Hải Châu, Đà Nẵng', to_timestamp('1476421079'), to_timestamp('1490207704'), to_timestamp('1490207704'));
INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Việt Food - Nhà Hàng Món Ăn Việt', 'Nhà hàng phong thái trang nhã, lịch sự. Khuôn viên thoáng, trang trí bởi nhiều hoa và đèn lồng tạo cảm giác ấm cúng, thân mật, sạch sẽ, phù hợp cho các buổi tiệc thân mật của gia đình, đồng nghiệp, và cả với người ấy nữa. Có nhiều góc tự sướng đúng điệu.
