INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Hải Sản Biển Mặn', 'Hải sản ở đây tươi rói, đang bơi ! Hải sản ở đây cực chất. Giá cả cũng rất bình dân. Cân đo đong đếm cũng chuẩn..không lo bị ăn gian đâu nhé. Món ăn chế biến ngon và đậm đà. Phục vụ cũng rất nhiệt tình và dễ thương. Chủ quán tư vấn rất kỹ, và sắp xếp món ăn cũng rất bài bản. Thích nhất là hải sản ở đây rất mặn mòi vị của biển. Hải sản tự chọn. Được nhân viên cân đo và tư vấn món ăn rất nhiệt tình. Nge nói toàn hải sản thuyền nhà đánh bắt nên chất lượng mới cao như vậy. Tôm tít to và gạch nhiều : ngọt và thơm. Ốc hương xào cũng rất thấm và đậm đà. Tôm chì hấp tuy chê biến đơn giản, nhưng mà ăn rất đậm đà và ngọt lịm nhưng rất mặn mòi vị biển. Sò mía nướng mỡ hành ăn vừa thơm hoà chút vị béo ngọt rất ngon. Mực lá chiên bơ thơm lừng. Ăn ngon và giòn nhưng vẫn giữ độ ngọt của mực. Cơm chiên trứng hạt rời và mềm. Ôi cái lẩu hải sản 170k mà sao nhiều dữ, 8 người ăn mà vẫn còn. Nói chung rất tuyệt vời cho một đêm họp mặt gia đình. Thưởng thức để cảm nhận ! Nhà mình ai cũng thật sự hài lòng dù đi hơi xa nhưng được lại một buổi tối rất ấm cúng và yêu thương. Có dịp sẽ ghé ủng hộ dài dài !', 3,4, '65 Trần Hưng Đạo, Quận Sơn Trà, Đà Nẵng', to_timestamp('1541224968'), to_timestamp('1562251118'), to_timestamp('1562251118'));
