INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Hanoi Taste - Lẩu Nướng Hà Thành', 'Chào bạn thichanlungtung. Là chủ quán mình xin gửi tới bạn lời xin lỗi vì phục vụ ko tốt của NV. Bạn có thể nói rõ cho mình nhân viên phục vụ tệ ở những điểm nào và NV phục vụ bạn ngày hôm đó là trai hay gái ? Gầy hay mập và nói giọng bắc hay trung đc ko? Để mình khiển trách và kỷ luật nhân viên đó vì lỗi nghiêm trọng của quán là NV ko đảm bảo đc chất lượng phục vụ. Mình xin tặng bạn voucher giảm 15% thay cho lời xin lỗi. Bản thân khi xây dựng quán Hà Nội để đảm bảo chuẩn hương vị Hà Nội thì quán ko thuê bếp , tất cả các các khâu sơ chế, chế biến , lựa chọn thực phẩm luôn luôn phải tự tay đi mua và chọn lựa để đảm bảo chất lượng đồ ăn cũng như an toàn VSTP. Và lòng là thứ mà sơ chế tốn time và công sức nhất , khi mua cũng phải chọn lựa rất kỹ để ko mua phải lòng bị đắng rất dễ mất khách. Còn về vấn đề lòng có sán, mình xin phép bạn gửi cho mình xin ảnh có sán ở trong đĩa lòng nhé. Với tư cách là chủ quán mình sẵn sàng đóng cửa quán nếu như khách cung cấp cho mình ảnh của sán trong lòng bán ở quán mình. Mình nghĩ có thể đã nhìn nhầm cái phần màu trắng ở phái trong lòng hay còn gọi là bột lòng , sữa lòng đó là sán. Vấn đề lớn nhất phải đối mặt khi quyết định đưa lòng phèo , dạ dày vào menu đó là SÁN và dĩ nhiên vẫn đề đó luôn đc chúng tôi quan tâm hàng đầu để đảm bảo chất lượng cũng như vệ sinh
Còn 1 điều nữa là hôm đó có phải bạn gọi 1 đĩa lòng nướng và để lại đến 1/2 đĩa phải ko? Vì chính mình đã ăn thử để xem có vấn đề gì ko? Mình phải nói vậy để bạn yên tâm chúng tôi luôn luôn kiểm soát đc chất lượng của món ăn dù ngày đông hay vắng khách.  Nguyên tắc của quán khi khách để lại đồ ăn thừa thì chính chủ quán và nhân viên sẽ phải thử ăn để kiểm tra xem lý do vì sao khách bỏ lại
Mong bạn hiểu và rất mong được phục vụ bạn lần nữa ....', 2,10, '92 Nguyễn Văn Thoại, Quận Ngũ Hành Sơn, Đà Nẵng', to_timestamp('1546592652'), to_timestamp('1563462739'), to_timestamp('1563462739'));
INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Đại Lộc 2 - Bánh Tráng Thịt Heo & Mì Quảng - Huỳnh Thúc Kháng', 'Bánh tráng thịt heo và cá hấp ngon lắm nhé. 
Mình gọi 1 phần bánh tráng thịt heo và 1 phần cá hấp. Cá to giá 55k/phần. Cá ngon thịt cũng ngon, nước chấm đậm đà. Có 1 loại nước chấm vàng vàng mình không biết tên bạn nhân viên nói chấm cái đó cho cá thì ngon hơn nhiều. 2 người ăn không hết luôn. Do mình đi lúc 2h chiều nên quán vắng và mì quảng các thứ cũng hết rồi. Quán sạch sẽ, nhân viên phục vụ cũng chu đáo. Nếu có dịp quay lại Đà Năng mình sẽ ăn lại món này. 😘😘😘', 1,3, '124 Huỳnh Thúc Kháng, Quận Hải Châu, Đà Nẵng', to_timestamp('1541469523'), to_timestamp('1563705584'), to_timestamp('1563705584'));
INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Nhà Hàng Biển Lớn ', 'Quán này khá tự hào với quy trình nuôi mực tươi sống tại quán luôn. Hôm bữa nghe gthieu nên mình cũng order món này- món nổi tiếng nhất của quán: mực cơm nhúng giấm. Mà tin ko? con mực tươi rói, đổi màu nhấp nhánh luôn á. Mực tươi nên ăn rõ ngọt luôn.
Hải sản ở đây bá tươi, giá theo thời giá nên đủ hiểu rồi. Tiền nào của đó, chất lượng cực kỳ nha mấy bạn. Thích cái tôm nướng ở đây, cái sốt phết ngoài lạ là mà thấm nữa. Còn tôm mũ ni bữa ăn 1 miếng là xỉu lun, thịt chắc và ngọt lắm. ăn vài miếng là ớn le rồi nè. Món này rang muối đúng ngon.
Ở đây có chả mực hạ long giã chứ ko xay, để miếng mực vẫn tròn vị và người ăn cảm nhận được. Món này khai vị thì hao bia cực kỳ.
Nhà hàng rộng đẹp bao la, mùa hè ngồi nhìn ra view biển bá cháy. Nhân viên đông và care khách cực kỹ nên dịch vụ làm mình khá là hài lòng.', 8,12, 'Lô 18 Võ Nguyên Giáp (Ngã 3 Võ Nguyên Giáp - Lê Văn Thứ), Quận Sơn Trà, Đà Nẵng', to_timestamp('1556188450'), to_timestamp('1562772842'), to_timestamp('1562772842'));
INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Maybe - Coffee & Drinks', 'Nước ngon, chất lượng không có gì để đắn đo. Nhưng mà nắp của ly nước quá lỏng, mình đặt 5 lần qua foody thì hết 3 lần shipper mua bị đổ nước, và 1 lần mình cầm ly ngay nắp và nắp lỏng quá thế là rơi đổ luôn cả ly 😭. Vậy nên vấn đề ở đây là nắp lỏng quá quán ơi !!! Mong quán có thể làm nắp chặt hơn để thuận tiện hơn cho cả người giao và người mua ạ 😍 Chốt lại thì nước vẫn cứ là ngon lắm nhé. Mọi người nên thử.', 4,7, '119 Hoàng Văn Thụ, Quận Hải Châu, Đà Nẵng', to_timestamp('1558273402'), to_timestamp('1563704951'), to_timestamp('1563704951'));
INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Cháo Lòng Cô Phúc', 'Pork offal porridge is quite a satisfying late afternoon mini-meal. The porridge here is just as good as any other, but what really stands out is the sauce. It''s quite good! It''s a little out of the way (I guess pork offal is not the most popular food ever), but if you are in the city center, it''s worth heading over this way. Ambience is alright; the place is rather spacious and it seems like a nice place to hang out and knock back a few beers. I don''t drink, though, so I''m quite happy to slurp down my porridge and be on my way.', 14,14, '91 Hoàng Hoa Thám, P. Tân Chính, Quận Thanh Khê, Đà Nẵng', to_timestamp('1452155974'), to_timestamp('1563703771'), to_timestamp('1563703771'));
INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Hot Hot - Mì Cay 7 Cấp Độ - Phan Tứ', 'Chả là chiều nay t có quán sau khi đi làm về gọi 3 tô : 2 hải sản ,1 mì gà . Cùng ăn với nhau mì cay nhưng t lại bị trúng thực nguyên nhân là do mì gà của quán này thề chiều nay ta chỉ ăn mì rồi về đi ngủ thôi.Thế mà lúc tối t có biểu hiện nóng người đau bụng đi ngoài các kiểu t chủ quan không đi khám một phần sợ ba mẹ lo.Hiện giờ là 1h đêm ta chịu không nổi nữa phải đi bệnh viện bác sĩ bảo là do trúng thực cần sốc ruột mới lòi ra là ăn gà bên ni .Mình viết lên đây cho mọi người né quán này ra thấy đông khách là thái độ như ko cần khách nghĩ mà tức sôi máu trong khi ngày mai còn phải đi làm', 14,6, '3 Phan Tứ , Quận Ngũ Hành Sơn, Đà Nẵng', to_timestamp('1551967786'), to_timestamp('1563703674'), to_timestamp('1563703674'));
INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Muối Ớt Xanh - Chân Gà Nướng & Cánh Gà Nướng', 'Dạo này mình đang thèm chân gà nướng nhưng lại chán với các quán quen thuộc như Hai Còi... Ăn không có gì mới mẻ thì tình cờ lướt Foody thấy quán này, đọc các bạn bình luận khá tốt nên tối nay rủ chị ghé ăn thử. Giá cả thì bình thường so với các quán khác, không gian cũng thoáng mở thoải mái, phục vụ khách vui vẻ, chị mình xin thêm dưa leo chị chủ rất nhiệt tình cho thêm nhiều lắm. Chân gà nướng giòn, ăn có vẻ ổn nhưng điều mình thích giống như bạn kia đánh giá là muối ớt xanh vị ngon, ăn kèm rất thích. Vì cơ bản món này ăn với tương hoài rất chán. Quán nói chung tạm ổn, có thể ghé lại nhưng mình mong quán chú ý thêm phần dưa leo, vì mình thấy ăn hơi đắng.', 4,12, '86 Nguyễn Thị Định, Quận Sơn Trà, Đà Nẵng', to_timestamp('1546242964'), to_timestamp('1563703658'), to_timestamp('1563703658'));
INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Dasushi - Ăn Nhậu Kiểu Nhật', 'Đây là một trong những nhà hàng Nhật mình hay lui tới. ở Đà Nẵng mình đã ăn nhiều nhà hàng Nhật nhưng đây là một trong những nhà hàng mình thích nhất, vị trí cũng ngay trung tâm thuận tiện đi lại. Nhà hàng đẹp, nhân viên phục vụ tốt, nhiệt tình. Món ăn đưa ra bàn nhanh. Hương vị món ăn rất tươi ngon đặc biệt những món sống như sashimi, (mình hay gọi sashimi cá hồi), trang trí món ăn cũng rất bắt mắt. Các món cơm cuộn, bánh xèo Nhật, udon (xào), ramen là những món mình hay gọi ở đây. Món nào cũng ngon. Chỉ có món bánh xếp Nhật lần trước gọi thử ăn hơi mặn một chút so với khẩu vị của mình. Nhưng nói chung các món ở đây đều ngon, rất đáng tiền. Nên thử.', 11,10, '266 - 268 Nguyễn Văn Linh, Quận Thanh Khê, Đà Nẵng', to_timestamp('1547485924'), to_timestamp('1561558812'), to_timestamp('1561558812'));
INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Ivy Cake - Shop Online', 'Phát hiện được quán này làm bánh kem bơ Hàn Quốc đẹp muốn xỉu luôn chời ơi. Shop online thôi nha, mà làm bánh chất lượng tuyệt đỉnh luôn á. Hôm bữa mình có đặt bánh ở đây, đến lấy bánh tranh thủ đứng xem mấy chị ở đây làm mà ta nói ghiền gì đâu á, thấy mê mẩn luôn. Chị chủ thân thiện, vui vẻ, còn cho mình bắt hoa thử nữa chứ, thích dễ sợ hà ^^
Bánh đẹp miễn bàn rồi, nhìn là không nỡ ăn. Mà ăn thì không thể ngừng lại, vì bánh ngon quá đi, hic. Cốt bánh red velvet màu đỏ, mềm xốp ăn ngon cực kỳ. Kem bơ mát lạnh, thơm thơm, béo béo nhưng không ngán như bánh kem truyền thống đâu. Bánh hơi mắc một chút, nhưng đảm bảo chất lượng và tốt cho sức khỏe.
Nhất định phải dồn tiền lại hồi ghé chỗ này học làm bánh thôi ^^', 11,7, '254 Điện Biên Phủ, Quận Thanh Khê, Đà Nẵng', to_timestamp('1472178576'), to_timestamp('1501232614'), to_timestamp('1501232614'));
