INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Khu Ẩm Thực Big C', 'Ở Đà Nẵng, nếu bạn hỏi tôi khu ẩm thực nào tuyệt vời nhất thì tôi chắc chắn sẽ trả lời là khu ẩm thực BigC của Đà Nẵng &lt;3
Nói thật vào đây, các bạn sẽ vô cùng sung sướng bởi vì nó không thiếu thứ gì trong đây cả, nào là bánh mỳ, nào là cơm, nào là gà...... vô số vô số để các bạn lựa chọn. Bởi vì cả nước đang lo ngại về vấn đề ngộ độc, an toàn thực phẩm, hàng kém chất lượng thì vào siêu thị BigC tất cả đều được đảm bảo. Giá ở đây quá trời rẻ, tuy nhiên chất lượng bánh vô cùng ngon. Sau khi oanh tạc hàng bánh mỳ, bạn có thể bước thêm 5-6 tới hàng ăn no, tới hàng bánh kem, tới hàng nước ngọt, tới gian hàng ăn vặt, ăn ngon, ......... vô số để các bạn lựa chọn. Nếu chưa tin, các bạn hãy đên khu ẩm thực tuyệt vời tự mình lựa chọn với vô số món và giá tiền không thể rẻ hơn này đảm bảo các bạn sẽ không thể thất vọng được !
À quên ngoài ẩm thực ra, bước nhẹ lên tầng 2 là bạn có thể mua được khá nhiều đồ, quần áo, đồ cho con mình, các dụng cụ và cả những mỹ phẩm chất lượng và rất hot nữa đấy nhé, quá tuyệt phải không nào hehe !', 5,1, 'Big C, 225 - 227 Hùng Vương, P. Vĩnh Trung, Quận Hải Châu, Đà Nẵng', to_timestamp('1466392744'), to_timestamp('1509899139'), to_timestamp('1509899139'));
INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
