INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Bánh Mì Phô Mai Đà Nẵng & Drink ', 'Mình từng ăn bánh trân châu ở đây rồi, trân châu mềm, lớp phô mai cũng được. Hôm nay đặt tiếp xem sao
