INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Akataiyo Mặt Trời Đỏ - Nguyễn Du', 'Bước chân đến quán lần đầu tiên đúng lúc có buffet ngon lành. Nằm ngay trên đường Nguyễn Du với phong cách rất Nhật Bản từ cách bài trí, phục vụ cho đến quầy bếp, Akataiyo thực sự là một điểm đến tốt cho những ai muốn trải nghiệm ẩm thực đất nước Mặt trời mọc này.
Không gian ấm cúng với bàn ghế trải đệm, dụng cụ ăn uống sắp xếp tỷ mỷ cẩn thận và ngọn đèn chong cao tạo cảm giác dễ chịu và hài lòng.
Nhân viên phục vụ vui tính, dễ thương và gợi ý kỹ càng món ăn theo mong muốn của mình. 
Món ăn thơm ngon và được trình bày hấp dẫn. Sashimi bạch tuộc giòn giòn ăn chung với lá tía tô và mù tạt thơm và cay. Cá hồi tái vị ngọt và mềm với chút cháy sém thơm thơm bên ngoài. Thêm vài lát gừng đỏ để sạch miệng và thế là sẵn sàng cho món mới. Takoyaki đặc với nhân đậm đà có chút mùi rượu, to và ngon hơn hẳn so với các tiệm từng ghé. Cơm cuộn to, cuốn chắc lưu lại toàn bộ hương vị thơm ngon. Sushi trứng và sushi cá hồi phomai rất ngon. Cơm vị giấm vừa độ, trứng mềm và dẻo, cá hồi thái đẹp vụ thơm. Nhìn miếng sushi nhỏ vậy thôi chứ ăn nguyên một lần thì rất nhiều. Thịt lợn giòn, thơm, sốt ngọt. Canh miso nhẹ nhàng, thơm với đậu phụ và rong biển, mát và lành.
Sushi được thực hiện ngay tại quầy bếp đối diện bàn ăn nên rất yên tâm về chất lượng. Giá cả khá phù hợp và món ăn thì tuyệt vời.', 1,8, '54 Nguyễn Du, Quận Hải Châu, Đà Nẵng', to_timestamp('1467639446'), to_timestamp('1560497877'), to_timestamp('1560497877'));
INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
