INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('The Pizza Company - Co.opMart Đà Nẵng', 'Mình cũng từng ăn pizza ở nhiều nơi nhưng để nói ăn đến mức được thẻ VIP thì chỉ có Pizza Company. Nhà mình là fan trung thành của hải sản nhiệt đới. Cứ hễ đến quán không cần kêu các bạn nhân viên cũng đã định hình được 😂 Đồ ăn ở đây thì ngon khỏi bàn rồi. Từ salad, thịt sườn cho đến pizza đều xứng đáng điểm 10. Chỉ có một phần trong menu mình không thích chính là: bánh mì bơ tỏi. Thật sự bánh mì bơ tỏi không có vị luôn á. Mình mong rằng quán sẽ cải thiện hơn 😢
Có một điểm mình khá không thích chính là việc Pizza Company nằm trong siêu thị Coop Mart. Việc nằm ở đây bên cạnh việc khó đi lại và giữ xe còn rất ồn ào. Thông thường các không gian của quán pizza sẽ nghiêm về sự yên tĩnh hơn. Nói chung đều đó không thể trách quán được. Chỉ là mình nói lên suy nghĩ của mình thôi. Còn lại từ món ăn đến nhân viên phục vụ đều rất ok. Các bạn đặc biệt quan tâm đến các khách hàng nhí cũng là một điểm cộng rất lớn 😍😍', 1,5, 'Tầng Trệt, Co.opMart, 478 Điện Biên Phủ, Quận Thanh Khê, Đà Nẵng', to_timestamp('1535551139'), to_timestamp('1563515122'), to_timestamp('1563515122'));
INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Cua Biển Quán - Hải Sản Bờ Biển', 'Quán ruột của nhà mình. Ăn hải sản phủ phê luôn mà rẻ nữa. Nếu ăn nhiều và đông, cứ gọi mỗi thứ nửa kg thì tính ra cũng ko bao nhiêu hết á. Được cái món ăn đa dạng, hải sản cực tươi và chế biến cực nhanh.
