INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Gangnam Du Chef - Món Hàn', 'Thật sự là cực kì thất vọng về quán này. Mình thấy trên fb có giới thiệu combo đặc biệt tháng 11 nên ghé thử quán mà ghé xong không hề muốn quay lại. 
