INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Hiền Eo - Cháo Quẩy Sườn Sụn', 'Không gian: quán nằm trong ngõ, chủ yếu là vỉa hè nhưng khá sáng sủa và có chỗ để xe, cũng có bàn trong nhà nhưng bé nên thôi cứ vỉa hè ngồi cho mát, tuy nhiên kiểu xe cộ đi qua hơi bụi tí 

