INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Bò Lê Lết Restaurant', 'Mình lướt foody tìm chỗ ăn thì vô tình thấy chỗ này dc đánh giá cao ngất nên đi ăn thử xem sao. Trời oiiii mình kbao giờ lên mạng viết review cái gì đâu nhưng mà hôm nay phải chụp lại để viết đánh giá cho cái nhà hàng này! 
