INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Cuisine De Vin - Bếp Nhà Vin', 'Mình là fan của Bếp Vin cũng phải gọi là khá lâu rồi. Đồ ăn ở đây thiên về vị tây hơn nên nhiều người nếu ăn không quen thì sẽ cảm thấy vị hơi không ngon. Thức ăn làm khi khách oder nên đồ ăn đem ra lúc nào cúng nóng hổi bù lại thì đợi hơi lâu một tí nhưng không sao, vì cái không gian ở đây cứ chậm rãi nhẹ nhàng mà lãng mạng, ngồi nói chuyện với nhau một lúc là có đồ ăn mang ra liền. Với là mọi người đừng nhìn thấy một đĩa ít đồ ăn mà nghĩ sẽ không no nha, hai người ăn gọi ba món như hình là vừa bụng luôn rồi í. Đặc biệt ở đây có món mì mực, mọi người nên ăn thử món đấy, theo mình thì thấy mì ngon. Bò ở đây tuỳ vào ai muốn ăn chín vừa hay chín tới thì cứ bảo với nhân viên lúc oder là được. Mì ý đút lò pho mai nhiều ứ ự. Giá cả hợp lí. Mình nghĩ mọi người nên đến đây vào những dịp quan trọng thì sẽ rất phù hợp, phải gọi là rất rất phù hợp luôn đấy nhé hehe.', 8,1, '176 Nguyễn Chí Thanh, Quận Hải Châu, Đà Nẵng', to_timestamp('1550063522'), to_timestamp('1563623652'), to_timestamp('1563623652'));
