INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Dasushi BBQ & Beer', 'Đây là lần đầu tiên mình cùng gia đình đi ăn ở đây . Về thức ăn cơ bản là ngon , mình thấy ở đây đồ khá là là hợp khẩu vị và không qúa khó ăn . Ban đầu mình kêu 1 phần trứng cá trích ép , trứng giòn , lát cá đẹp nhưng hình như đồ không được tươi lắm , ăn không mấy có vị . Tiếp sau đó thì gọi một phần sushi tươi sống+ kimpap ăn khá ok . Cuối cùng gia đình mình có gọi thêm một phần thịt nướng . Thịt khá ngon và thấm , ăn béo và rất thơm mình thích món thịt hơn cả .Tuy nhiên điểm trừ duy nhất ở đây là cách phục vụ, ở gogi hay nhiều chỗ mình ăn nhân viên đều nướng thịt cho khách , mình đi tầm trưa và quán khá vắng nhân viên thì đứng đó chả biết làm gì để mẹ mình ngồi nướng và bàn ăn lúc mình mới vào nhìn khá dơ nhưng cũng không thấy ai tới lau , sau mình kêu nhân viên lau bàn thì bàn mới bớt dơ . Hy vọng quán sẽ cải thiện !!!!', 14,2, 'Lô A11 - 01 Võ Văn Kiệt, Quận Sơn Trà, Đà Nẵng', to_timestamp('1523185231'), to_timestamp('1552047518'), to_timestamp('1552047518'));
INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
