INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Lươn Xứ Nghệ Dì Mai - 149 Châu Thị Vĩnh Tế', 'Quán nằm ở khu du lịch An Thượng nên rất thuận tiện đi lại và dễ tìm, không gian quán rộng rãi, sạch sẽ, có thể ngồi được tầm 30 người trong nhà và ngoài sân, có chỗ đỗ xe thoải mái.
