INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Xiên Que - Nguyễn Thị Minh Khai', 'Ấn tượng ban đầu của mình về quán là không gian ấm cúng, đồ ăn đa dạng phong phú. Hồi ấy còn có chương trình cho mặc thử yukata nữa. Còn bây giờ thì có nhiều món ngon hơn, nhưng đồ ăn thì ít bớt đi, không thấy sự đa dạng nữa.
Một điều đặc biệt là cho dù có ăn bao nhiêu lần thì mình vẫn sẽ gọi bánh xèo Osaka. Bởi vì nó được chế biến cực kì ngon. Dù thời gian đợi món ấy có chút lâu. Tầm 30p gì đấy. Nếu muốn ăn bánh xèo thì nên ăn ít đồ ăn ở phía trước lại, vì bánh khá to và ăn rất dễ ớn. Còn Takoyaki rất ngon. Phần nhân thì nhiều, vỏ bánh mỏng, ăn không hề ớn. Mình thích nhất là ăn hộp bento. Con thỏ rất xinh, không nỡ ăn xíu nào luôn :<< Nhưng mà thực sự ăn bento thì dễ no, đồ ăn nhiều. Ăn hết cơm rồi mà đồ ăn vẫn còn nhiều lắm. Nhưng nà nếu so về giá thì ở đây bán khá mắc ấy. 2 người mà ăn gần 300k rồi mới no cơ. Chủ yếu là do mấy món xiên. Còn bánh xèo, bento hay mì cay thì vẫn còn rẻ. Mình đặc biệt thích ăn cái sốt màu trắng. Chấm với đồ ăn thì ngon cực kì.', 2,1, '138 Nguyễn Thị Minh Khai, Quận Hải Châu, Đà Nẵng', to_timestamp('1542968879'), to_timestamp('1563512584'), to_timestamp('1563512584'));
INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Trà Sữa Ocha House - Phan Đăng Lưu', 'Mình uống ở đây hoài trà sữa olong với phần ocha2 ( trên menu thể hiện: trân châu đen, phô mai, pudding). Vậy mà lúc nào quán cũng “ khuyến mãi” thêm thạch nhỏ nhỏ xíu xíu ( mà mình hỏi thì nói thạch ocha).
