INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Bún Đậu Cô Hạnh', 'Rất thích đồ ăn của quán. Lần đầu tiên ăn quán bún đậu nào mà 1 mẹt 30k lại thấy đầy ắp, no cả bụng lại vừa ngon miệng nữa. Không gian quán thì mát mẻ. Nhưng mà để ghé lại quán thì không, chỉ order về nhà ăn thôi. Mình nghĩ đã là quán ăn thì hạn chế nuôi động vật, đặc biệt không phải 100% ai cũng yêu, cũng thích động vật, ở quán thì không chỉ 1 con mèo mà là cả đống mèo. Ngồi ăn mà mèo đi quanh. Mình nhờ nhân viên cất mèo lại thì nhân viên lấy tay xách cổ mèo đưa vào trong, rồi thì y cái bàn tay đó bưng đồ ăn đến cho khách, và mèo thì lại tiếp tục đi ra. Nên thôi, mình không thích mèo thì cũng đành tạm biệt quán.', 11,2, '153 Nguyễn Du, Quận Hải Châu, Đà Nẵng', to_timestamp('1555170412'), to_timestamp('1561979438'), to_timestamp('1561979438'));
INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Golden Phoenix Wedding & Convention Center', 'Mình đi đám cưới nhiều lần ở đây k có lần nào để than phiền về cung cách phục vụ, không gian của nhà hàng hết. Nhà hàng có không gian sang trọng decor đẹp mắt, phục vụ tận tình chu đáo, chỗ để xe rộng rãi thoải mái. Về món ăn thì mình cũng biết chất lượng món ăn tuỳ vào mỗi suất ăn nhưng mình đi nhiều lần chưa bao giờ thấy đồ ăn ở đây dở hết, rất ngon hoặc cũng vừa ăn, lần gần đây nhất thì chắc bạn mình chọn thức ăn kĩ nên đồ ăn rất rất ngon luôn. Về giá cả thì mình biết là cao hơn những chỗ khác nhưng xứng với đồng tiền bỏ ra. Nếu có điều kiện thì mình nghĩ tổ chức ở đây rất đáng, vì đám cưới rất quan trọng đối với mỗi người mà hí hí. Tóm lại sau này tổ chức đám cưới mình hi vọng có thể tổ chức ở đây :))).', 10,3, 'A8.2 Khu công viên Bắc tượng đài Đường 2 Tháng 9, Quận Hải Châu, Đà Nẵng', to_timestamp('1453700053'), to_timestamp('1542694698'), to_timestamp('1542694698'));
INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Gà Rán KFC - Nguyễn Thị Minh Khai', 'Mình đi nhóm 4 người, đến quán khoảng 9h30. Vì 10h mới có cơm trưa nên 2 bạn order 2 phần gà ăn trc, mình và 1 bạn khác tính đợi đến 10h sẽ order 2 phần cơm. Lúc đó bạn nhân viên (nghe 1 bạn nhân viên khác gọi tên là Yến) tỏ ra rất coi thường, dù lúc tụi mình thống nhất như vậy bạn ấy đã nghe hết nhưng vẫn hỏi lại là "chị đi 4 người mà gọi có 2 gà sao?". Sau đó lúc tụi mình bàn nên ăn gà gì vì ko biết hết thì bạn ấy vẫn đứng nhìn chằm chằm cả đám với vẻ khinh thường, ko thèm tư vấn có những loại nào, đến lúc mình quay sang hỏi mới chịu nói. Sau đó tụi mình ngồi đợi gà, 5ph sau mình quay sang quầy thấy gà đã đầy trong tủ nhưng ko thấy mang ra, nửa tiếng sau lại hỏi một bạn nhân viên nam thì bạn ấy nge xong đi thẳng ko thèm nói gì, sau đó mở tủ lấy gà mang đến, cũng không hề nói gì với tụi mình. Không biết tụi mình có phải trường hợp đặc biệt không nhưng hy vọng KFC sớm cải thiện tình trạng nhân viên, vì với mình đây không phải lần đầu.', 3,4, '116 Nguyễn Thị Minh Khai, Quận Hải Châu, Đà Nẵng', to_timestamp('1507992777'), to_timestamp('1563509274'), to_timestamp('1563509274'));
INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Boardgame Cafe', 'Mình biết đến quán thông qua em mình. Vì cũng gần nhà nên hôm nọ cũng đến chơi thử, nhóm mình đi tầm 7 người. Ở đây boardgame khá phong phú, không gian khá ổn để ngồi chơi nhóm đông người. Đồ uống cũng tạm được.
Tuy nhiên mình không thích cách phục vụ của quán. Hôm mình đến quán chỉ có tầm 2 nhóm người và cũng đã có đồ uống, nhưng không hiểu sao nhóm mình ngồi chơi những 30p vẫn chưa có nước và phải đi hỏi những 2 lần mà vẫn chưa có ?!? Chưa kể ngồi chơi game nói khô cả cổ nhưng đến nước lọc cũng không thấy nhân viên quán mang lên, lúc sau mình có nhắc nhưng đến cuối giờ mình đi về vẫn không hề có nước lọc dù mình đã nói ?!? 
Nếu là nơi để mua boardgame về chơi thì đây là địa chỉ khá ổn vì game đa dạng nhưng để mình ghé quán uống nước và chơi board game thì mình sẽ không quay lại :)', 5,12, '134 Nguyễn Đức Trung, Quận Thanh Khê, Đà Nẵng', to_timestamp('1549095035'), to_timestamp('1563509257'), to_timestamp('1563509257'));
INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Why Roastery Coffee', 'Mình đi quán này được 3 lần, ấn tượng hai lần đầu phải nói là quá tốt, cực kỳ tốt, so với mặt bằng thị trường cafe như Đà Nẵng hiện tại, có một quán đảm bảo đủ về không gian và chất lượng như Why Roastery là không phải dễ. Nhưng, đến lần thứ ba thì, hơi không hài lòng một chút. Cafe không ngon như hai lần đầu, mở nhạc quá to, và đặc biệt là, quán trở nên ồn ào hơn hẳn. Quán đông khách là một điều tốt, nhưng bản thân mình cảm thấy quán đang dần mất đi cái chất ban đầu. Và quan trọng hơn là, chất lượng cafe đang đi xuống nhé. Hy vọng lần sau ghé lại, sẽ có một cái nhìn tích cực hơn :)', 1,6, '64 Ung Văn Khiêm, Quận Ngũ Hành Sơn, Đà Nẵng', to_timestamp('1494840461'), to_timestamp('1543553068'), to_timestamp('1543553068'));
INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Tiệm Ăn Vua Vịt Quay - Phạm Cự Lượng', 'Ấn tượng là nhân viên và chủ quán rất nhiệt tình với khách. Lúc đầu cứ tưởng là nhà hàng to hay gì cơ ai ngờ lúc đến thì giống mấy tiệm cơm của trung quốc ấy. Không gian nhỏ nhưng được cái mát mẻ. Chị  chủ tư vấn cho 2  đứa mẹt combo 2 người 109k gồm vịt quay, gà quay, cơm rang, bánh hỏi, 1 bát cháo, 1 bát canh, kim chi. Cơm ngon, gà vịt quay vừa ăn không ngán lắm. Gà hơi bở còn vịt thì không chê. Còn được tặng thêm nước sâm. Cơm ngon nên phải gọi thêm 1 dĩa nữa ăn mới thấm tháp. Sẽ quay lại vì ưng thái độ phục vụ ^^', 1,2, '174 Phạm Cự Lượng, Quận Sơn Trà, Đà Nẵng', to_timestamp('1544024931'), to_timestamp('1563507315'), to_timestamp('1563507315'));
INSERT INTO activity ( name, description, created_by_user_id, updated_by_user_id, address, created_date, updated_date, active_date)
VALUES ('Thịt Rừng Tây Bắc Hoà Sơn', '-Quán này khá nổi tiếng trong giới ăn nhậu nè. Nằm khá xa trung tâm thành phố (đường lên nghĩa trang Hòa Sơn). Đường rộng và to nên đi khá nhanh. Đến nơi mình khá bất ngờ vì quán khá đông mà 1 đoàn dài toàn là ô tô cả thôi. Phục vụ ở đây cực chuyên nghiệp lun. thoáng 1 cái là nhảy ra tư vấn đủ các kiểu. Nhưng giá ở đây phải gọi là "cắt cổ" thôi rồi. PR chồn hương mà 1 kg là 1 triệu 8 (bạn mình nói quá mắc so với nhưng chỗ khác). Chưa kể, ở đây menu cũng ko có giá cả rõ ràng. Lưu ý mọi người nếu có đến quán thì nên dạo giá trước. Mình gọi 1 um ốc bưu đen (ốc khá nhỏ, ko đặc biệt lắm) mà những 80k. Ếch núi rang lá chanh thì cũng chẳng có gì là đặc biệt. Nhưng rất hãi khi tính đến gần 40k/ con. Thấy có vịt trời lạ là order thử, vịt nhỏ xíu xiu dọn ra chút é. Thịt cũng ko ngon như vịt thường ăn. chỉ bằng 1/3 còn vịt bình thường mà tính đến 400k/con thặc kinh khủng. Hèn gì đến cuối buổi thì dọn 1 đĩa trái cây free. Nói chung ăn cho biết chứ thấy ko có ngon mà giá cực kỳ đăt.', 12,14, 'X. Hoà Sơn, Quận Liên Chiểu, Đà Nẵng', to_timestamp('1417968358'), to_timestamp('1563506785'), to_timestamp('1563506785'));
