INSERT INTO comment ( content, date_create, user_id, activity_id)
VALUES ('Quán này nhà mình hay ghé ăn cuối tuần vì đồ ăn quán khá hợp gu với gia đình mình, như món bắp bò thường khá là mềm. Nhà mình thường đặt 7-8 người ăn giá cũng không rẻ nên hôm hay mình cực kì không hài lòng. Thường thì mình thấy nhân viên phục vụ không chuyên nghiệp lắm nhưng có thể bỏ qua, nhưng chuyện hôm nay thì không chấp nhận được nên đây cũng sẽ là lần đầu tiên mình đánh giá dịch vụ 1*. 
