INSERT INTO comment ( content, date_create, user_id, activity_id)
VALUES ('Mình đi lúc 5h vẫn chưa có khách, không gian rộng rãi và sạch sẽ.Nhân viên chu đáo. Chắc vẫn chưa có khách nên khi dọn món rất nhanh nha. 
